jectSorting)- (void)insertSortedObject:(id<CCSortableObject>)object {    CFIndex idx;        idx = [self indexOfSortedObject:object];        [self insertObject:object atIndex:idx];}- (void)removeSortedObject:(id<CCSortableObject>)object {    if ([self count] == 0) {        return;    }    CFIndex idx;    id<CCSortableObject> foundObj;    idx = [self indexOfSortedObject:object];        if (idx < [self count] && idx >= 0) {        foundObj = (id<CCSortableObject>)[self objectAtIndex:idx];                if([foundObj objectID] == [object objectID]) {            [self removeObjectAtIndex:idx];        }    }}- (void)setObjectID:(NSUInteger)tag ofSortedObject:(id<CCSortableObject>)object {    id<CCSortableObject> foundObj;    CFIndex  idx;        idx = [self indexOfSortedObject:object];    if (idx < [self count] && idx >= 0) {        foundObj = [self objectAtIndex:idx];                if([foundObj objectID] == [object objectID]) {            [self removeObjectAtIndex:idx];            [foundObj setObjectID:tag];            [self insertSortedObject:foundObj];        }    }}- (id<CCSortableObject>)objectWithObjectID:(NSUInteger)tag {    if ([self count] == 0) {        return nil;    }        CFIndex idx;    id<CCSortableObject> foundObj;        foundObj = [[CCSortedObject alloc] init];    foundObj.objectID = tag;        idx = [self indexOfSortedObject:foundObj];    foundObj = nil;        if (idx < [self count] && idx >= 0) {        foundObj = [self objectAtIndex:idx];        if (foundObj.objectID != tag) {            foundObj = nil;        }    }        return foundObj;}- (CFIndex)indexOfSortedObject:(id<CCSortableObject>)object {    CFIndex idx;    if (object) {        idx = CFArrayBSearchValues((CFArrayRef)self, CFRangeMake(0, [self count]),                                   (void *)object, &_compareObject, NULL);    } else {        idx = NSNotFound;    }    return idx;}@end#pragma mark -@implementation CCArray (SWObjectSorting)- (void)insertSortedObject:(id<CCSortableObject>)object {    CFIndex idx;        idx = [self indexOfSortedObject:object];        [self insertObject:object atIndex:idx];}- (void)removeSortedObject:(id<CCSortableObject>)object {    if ([self count] == 0) {        return;    }    CFIndex idx;    id<CCSortableObject> foundObj;    idx = [self indexOfSortedObject:object];        if (idx < [self count] && idx >= 0) {        foundObj = (id<CCSortableObject>)[self objectAtIndex:idx];                if(foundObj.objectID == object.objectID) {            [self removeObjectAtIndex:idx];        }    }}- (void)setObjectID:(NSUInteger)tag ofSortedObject:(id<CCSortableObject>)object {    id<CCSortableObject> foundObj;    CFIndex  idx;        idx = [self indexOfSortedObject:object];    if (idx < self.count && idx >= 0) {        foundObj = [self objectAtIndex:idx];                if (foundObj.objectID == object.objectID) {            [self removeObjectAtIndex:idx];            [foundObj setObjectID:tag];            [self insertSortedObject:foundObj];        }    }}- (id<CCSortableObject>)objectWithObjectID:(NSUInteger)tag {    if ([self count] == 0) {        return nil;    }        CFIndex idx;    id<CCSortableObject> foundObj;        foundObj = [[CCSortedObject alloc] init];    [foundObj setObjectID:tag];        idx = [self indexOfSortedObject:foundObj];    foundObj = nil;        if (idx < self.count && idx >= 0) {        foundObj = [self objectAtIndex:idx];        if (foundObj.objectID != tag) {            foundObj = nil;        }    }        return foundObj;}- (CFIndex)indexOfSortedObject:(id<CCSortableObject>)object {    CFIndex  idx;    if (object) {        idx = CFArrayBSearchValues((CFArrayRef)self, CFRangeMake(0, [self count]),                                   (void *)object, &_compareObject, NULL);    } else {        idx = NSNotFound;    }        return idx;}@end